SELECT i.InvoiceId, a.Name, l.Title, t.Name, g.Name, m.Name, t.UnitPrice FROM Artist a JOIN Album l on a.ArtistId=l.ArtistId JOIN Track t on t.AlbumId=l.AlbumId JOIN MediaType m on t.MediaTypeId=m.MediaTypeId JOIN Genre g on t.GenreId=g.GenreId LEFT JOIN InvoiceLine i on t.TrackId=i.TrackId WHERE i.InvoiceId IS NULL AND   l.Title ="Frank" OR l.Title="Revelations"  GROUP BY a.Name, l.Title, t.Name, g.Name, m.Name, t.UnitPrice ORDER BY a.Name desc;